import React, { useEffect, useState } from "react";
import './AddForm.css'
import { useSelector } from "react-redux"
import { useDispatch } from "react-redux";
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import TextField from '@mui/material/TextField';
import DialogTitle from '@mui/material/DialogTitle';
import Button from 'react-bootstrap/Button';

function AddForm() {
    const popupOpen = useSelector((state) => state.popupOpen);
    const dispatch = useDispatch();
    const [employee, setEmployee] = useState({ "id": "", "name": "", "email": "", "designation": "", "experience": "" });

    return (
        <div >
            <Dialog open={popupOpen} onClose={() => dispatch({ type: "CLOSE_POPUP" })}>
                <DialogTitle>Enter Employee Details</DialogTitle>
                <DialogContent>
                    <TextField autoFocus margin="dense" id="Name" label="Name" type="text" fullWidth variant="standard" onChange={(e) => fnChangeFormValues(e)} value={employee.name} />
                    <TextField autoFocus margin="dense" id="Email" label="Email" type="text" fullWidth variant="standard" onChange={(e) => fnChangeFormValues(e)} value={employee.email} />
                    <TextField autoFocus margin="dense" id="Designation" label="Designation" type="email" fullWidth variant="standard" onChange={(e) => fnChangeFormValues(e)} value={employee.designation} />
                    <TextField autoFocus margin="dense" id="Experience" label="Experience" type="text" fullWidth variant="standard" onChange={(e) => fnChangeFormValues(e)} value={employee.experience} />
                </DialogContent>
                <DialogActions>
                    <Button >Add</Button>
                </DialogActions>
            </Dialog>
        </div >
    );
}

export default AddForm;
