import { gql } from "@apollo/client";

export const LOAD_USERS = gql`
  query {
    getAllUsers {
      id
      Name
      email
      designation
      experience
    }
  }
`;



export const CREATE_USER_MUTATION = gql`
  mutation createUser(
    $firstName: String!
    $lastName: String!
    $email: String!
    $designation: String
  ) {
    createUser(
      firstName: $firstName
      lastName: $lastName
      email: $email
      designation: $designation
    ) {
      id
    }
  }
`;
