import {
    ADD_EMPLOYEE,
    DELETE_EMPLOYEE,
    OPEN_POPUP,
    CLOSE_POPUP

} from '../Action';
import { useQuery, gql } from "@apollo/client";
import { LOAD_USERS } from '../Graphql/Queries'
// import { employee } from '../Data';

const { error, loading, data } = useQuery(LOAD_USERS);
const initialState = {
    popupOpen: false,
    aEmployee: []
}

const reducer = (state = initialState, action) => {
    if (action.type === ADD_EMPLOYEE) {
        var newEployee = [...state.aEmployee, action.value];
        return {
            aEmployee: newEployee
        }
    }
    // if (action.type === DELETE_EMPLOYEE) {
    //     var deleteEployee = [...state.aEmployee];
    //     deleteEployee.splice(action.index, 1);
    //     return {
    //         aEmployee: deleteEployee
    //     }
    // }
    if (action.type === OPEN_POPUP) {
        return {
            popupOpen: true
        }
    }
    if (action.type === CLOSE_POPUP) {
        return {
            popupOpen: false
        }
    }
    return state;
}

export default reducer;
